/**
 * 定义 {@link EventDispatcher} 派发的事件对象。
 * @see {@link EventDispatcher}
 * @since 1.0.0
 */
export default class Event {
    /**
     * 创建一个事件对象。
     * 
     * @param {String|Symbol} type - 事件类型。
     * @param {Boolean} [bubbles=false] - 指示该事件是否参与冒泡行为。
     * @param {Boolean} [cancelable=true] - 指示该事件是否可以取消默认行为。
     * @since 1.0.0
     */
    constructor( type, bubbles = false, cancelable = true ) {
        /**
         * @type {String|Symbol}
         */
        this._type = type;

        /**
         * @type {Boolean}
         */
        this._bubbles = bubbles;

        /**
         * @type {Boolean}
         */
        this._cancelable = cancelable;

        /**
         * @type {IEventDispatcher}
         */
        this._target = null;

        /**
         * @type {IEventDispatcher}
         */
        this._currentTarget = null;

        /**
         * @type {EventPhase}
         */
        this._eventPhase = EventPhase.NONE;

        /**
         * @type {Boolean}
         */
        this._defaultPrevented = false;

        /**
         * @type {Boolean}
         */
        this._stopPropagation = false;

        /**
         * @type {Boolean}
         */
        this._stopImmediatePropagation = false;
    }

    /**
     * 获取事件类型。
     * @type {String|Symbol}
     * @since 1.0.0
     */
    get type() {
        return this._type
    }

    /**
     * 指示该事件是否参与冒泡行为。
     * @type {Boolean}
     * @since 1.0.0
     */
    get bubbles() {
        return this._bubbles;
    }

    /**
     * 指示该事件是否可以取消默认行为。
     * @type {Boolean}
     * @since 1.0.0
     */
    get cancelable() {
        return this._cancelable;
    }

    /**
     * 获取事件的目标对象。
     * @type {IEventDispatcher}
     * @since 1.0.0
     */
    get target() {
        return this._target;
    }

    /**
     * 获取事件的当前对象。
     * @type {IEventDispatcher}
     * @since 1.0.0
     */
    get currentTarget() {
        return this._currentTarget;
    }

    /**
     * 获取事件的当前阶段。
     * @type {EventPhase}
     * @since 1.0.0
     */
    get eventPhase() {
        return this._eventPhase;
    }

    /**
     * 指示该事件是否已经取消了默认行为。
     * @type {Boolean}
     * @since 1.0.0
     */
    get defaultPrevented() {
        return this._defaultPrevented;
    }

    /**
     * 如果事件可以取消(`cancelable == true`)默认行为，则取消该事件的默认行为。
     * @see {@link Event#cancelable}
     * @since 1.0.0
     */
    preventDefault() {
        this._cancelable && (this._defaultPrevented = true);
    }

    /**
     * 停止事件冒泡，后续所有节点的侦听器都将不会收到该事件通知。
     * @see {@link EventDispatcher#dispatchEvent}
     * @since 1.0.0
     */
    stopPropagation() {
        this._stopPropagation = true;
    }

    /**
     * 立即停止事件冒泡，当前节点以及后续所有节点的侦听器都将不会收到该事件通知。
     * @see {@link EventDispatcher#dispatchEvent}
     * @since 1.0.0
     */
    stopImmediatePropagation() {
        this._stopPropagation = true;
        this._stopImmediatePropagation = true;
    }
}

