     * @since 1.0.0
     */
    constructor( scope = null, once = false, useCapture = false, priority = 0 ) {
        /**
         * @type {IEventDispatcher}
         */
        this._scope = scope;

        /**
         * @type {Boolean}
         */
        this._once = once;

        /**
         * @type {Boolean}
         */
        this._useCapture = useCapture;

        /**
         * @type {Number}
         */
        this._priority = priority;
    }

    /**
     * 获取侦听器的作用域对象。
     * @type {IEventDispatcher}
     * @since 1.0.0
     */
    get scope() {
        return this._scope;
    }

