            if ( options.once !== void 0 ) { this._once  = options.once; }
            if ( options.useCapture !== void 0 ) { this._useCapture = options.useCapture; }
            if ( options.priority !== void 0 ) { this._priority = options.priority; }
        }

        return this;
    }
}

